import { combineReducers, createStore } from "redux";
import reducerA from './reducerA';

export default (preloadState, options) => {
  return createStore(
    combineReducers({
      reducerA,
      reducerB
    }),
    preloadState,
    window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
  )
}
